import CryptoJS from "crypto-js"; //秘钥

var CRYPTOJSKEY = "6920D041374F2BC8";
export default {
  //加密

  /*
  * {param} plaintText 加密明文
  * return  str 加密结果
  */
  encrypt: function encrypt(plaintText, cryptjskey) {
    var plaintText = plaintText;
    var options = {
      mode: CryptoJS.mode.ECB,
      padding: CryptoJS.pad.Pkcs7
    };
    var key = CryptoJS.enc.Utf8.parse(CRYPTOJSKEY);
    var encryptedData = CryptoJS.AES.encrypt(plaintText, key, options);
    var encryptedBase64Str = encryptedData.toString();
    return encryptedBase64Str;
  },
  //解密

  /*
  * {param} plaintText 解密密文
  
  * return  str 解密结果
  */
  decrypt: function decrypt(encryptedBase64Str, cryptjskey) {
    var encryptedBase64Str = encryptedBase64Str;
    var options = {
      mode: CryptoJS.mode.ECB,
      padding: CryptoJS.pad.Pkcs7
    };
    var key = CryptoJS.enc.Utf8.parse(CRYPTOJSKEY); // 解密

    var decryptedData = CryptoJS.AES.decrypt(encryptedBase64Str, key, options); // 解密后，需要按照Utf8的方式将明文转位字符串

    var decryptedStr = decryptedData.toString(CryptoJS.enc.Utf8);
    return decryptedStr;
  }
};
