var websock = null;
var state = -1;
import vuex from "../store/index";
export default {
  initWebSocket: function initWebSocket() {
    var _this = this;

    //初始化weosocket
    var token = sessionStorage.token;

    if (token) {
      var ds = window.encodeURIComponent(token); // const wsuri = `ws://192.168.2.105:8080/EasRobotWS/ws?token=${ds}`;

      var wsuri = "ws://ws.lhds.vip:8080/EasRobotWS/ws?token=".concat(ds);
      websock = new WebSocket(wsuri);
      state = websock.readyState;

      websock.onmessage = function (e) {
        _this.websocketonmessage(e);
      };

      websock.onopen = function () {
        _this.websocketonopen();
      };

      websock.onerror = function () {
        console.log('失败了');
      };

      websock.onclose = function (e) {
        _this.websocketclose(e);
      };
    }
  },
  websocketonmessage: function websocketonmessage(e) {
    var result = JSON.parse(e.data);
    console.log('收到数据 = >', result);
    state = websock.readyState;
    vuex.state.collocationList = result;
  },
  websocketonopen: function websocketonopen() {
    console.log('打开websocket'); //this.websocketsend();

    state = websock.readyState;
  },
  websocketclose: function websocketclose(e) {
    //关闭
    console.log('断开连接', e);
    vuex.state.collocationList = [];
    state = -1;
  },
  websocketsend: function websocketsend(Data) {
    //数据发送
    console.log('发送数据 = >', Data);
    websock.send(Data);
  },
  close: function close() {
    if (state >= 0) {
      websock.close();
      state = websock.readyState;
    }
  },
  lookState: function lookState() {
    //查询sock当前状态
    return state;
  }
};
