import { scrollMixins } from "../../mixins/scroll.js";
export default {
  name: 'toast',
  props: ['message', 'title', 'visible', 'hideOk', 'hideCancel'],
  mixins: [scrollMixins],
  //弹出时不允许滚动
  data: function data() {
    return {
      dialog: false
    };
  },
  watch: {
    visible: {
      handler: function handler(nV, oV) {
        this.dialog = nV;

        if (nV) {
          //显示时不允许遮罩下滚动
          this.afterOpen();
        } else {
          //隐藏后允许滚动
          this.beforeClose();
        }
      },
      immediate: true
    }
  },
  methods: {
    okClick: function okClick() {
      //js里的方法名要跟这个相同
      this.$emit('onOk');
    },
    cancelClick: function cancelClick() {
      this.$emit('onCancel');
    }
  }
};
