export default {
	data() {
		return {
			valid:true,

			rules: {
				required: v => !!v || '必填',
				size: v => v > 0 || '必须大于0',
				percent:v => v <= 100 || '不可超出100'
			},

			other:['补仓','止损','建仓'],

			currentObj:{}


		}
	},
	props: {
		dialog: {
			type: Boolean,
			default: false
		},
		newOtherObj: {
			type:Object,
			default:() =>({})
		}
	},
	watch:{
		newOtherObj:{
			handler(nV){
				this.currentObj = {...nV};
			},
			immediate:true,
			deep:true
		}
	},
	methods:{
		cancelOtherRule(){
			this.$emit('cancelDialog');
			this.$refs.other.resetValidation();
			this.currentObj = {};
		},

		newOtherRule(){
			if(this.$refs.other.validate()){
				this.$emit('pushData',this.currentObj);
				this.$refs.other.resetValidation();
			}

		},

	}
}
